import { React, useState, useEffect } from "react";
import { Form, Button, Col, Row, Container, Table } from "react-bootstrap";

function AnswerBox () {
    const [answerMessage, setAnswerMessage] = useState("");
    const [userAnswer, setUserAnswer] = useState([]);

    const checkAnswer = (event) => {
        const correctAnswer = 53.1;
        setUserAnswer(userAnswer);

        if (userAnswer <= correctAnswer ) {
            setAnswerMessage("Correct!")
        
        } else {
            setAnswerMessage("Incorrect. Please try again!")
        }
    }



    return (
        <Table size="sm">
            <thead>
                <tr>
                    <th colSpan="2">
                        Solve for angle b (in degrees)
                    </th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                        Answer =
                    </td>
                    <td>
                        <Form.Label className="mx-auto">
                            <Form.Control
                                required
                                placeholder="Round to 1 decimal. (e.g. 10.3)"
                                type="number"
                                value={userAnswer}
                                onChange={(e) => 
                                    setUserAnswer(e.target.value)}
                                >
                            </Form.Control>
                        </Form.Label>
                    </td>
                </tr>
                <tr>
                    <td colSpan="2">
                        <Button
                            onSubmit = {(submittedAnswer) => checkAnswer(submittedAnswer)}
                            >
                            Check Answer
                        </Button>
                    </td>   
                </tr>
            </tbody>
        </Table>
    );
};

export default AnswerBox;
