import { React, useState, useEffect } from "react";
import { Form, Button, Toast, Row, Col } from "react-bootstrap";

function AnswerBox () {
    const [answerMessage, setAnswerMessage] = useState("");
    const [userAnswer, setUserAnswer] = useState("");
    const [showToast, setShowToast] = useState(false);

    const toggleShowToast = () => setShowToast(!showToast);

    const checkAnswer = (event) => {
        event.preventDefault();
        const correctAnswer = 53.1;
        if (userAnswer <= correctAnswer + 0.1 && userAnswer >= correctAnswer - 0.1) {
            setAnswerMessage("Correct! Try another problem!")
        } else {
            setAnswerMessage("Incorrect. Please try again!")
        };
    };

    return (
        <Form onSubmit={checkAnswer}>
            <br></br>
            <Row className="align-items-center">
                <Col>
                    <h3>
                        Solve for angle b
                    </h3>
                </Col>
            </Row>
            <Row className="align-items-center">
                <Col md={4}>
                    Answer
                </Col>
                <Col md={4}>
                    <Form.Text className="text-muted">
                        in degrees
                    </Form.Text></Col>
                <Col mb={6}>
                    <Form.Label>
                        <Form.Control
                            required
                            placeholder="Round to 1 decimal. (e.g. 10.3)"
                            type="number"
                            step="0.1"
                            min="0"
                            max="180"
                            value={userAnswer}
                            onChange={e => setUserAnswer(e.target.value)}
                            >
                        </Form.Control>
                        <Form.Control.Feedback type="invalid">
                            Please enter a numeric value with up to 1 decimal.
                        </Form.Control.Feedback>
                    </Form.Label>
                </Col>
            </Row>
            <br></br>
            <Row>
                <Col sm={{ span: 10, offset: 1 }}>
                    <Button 
                        type="submit"
                        onClick={toggleShowToast}
                        >
                        Check Answer
                    </Button>
                    <Toast 
                        show={showToast}
                        onClose={toggleShowToast}
                        >
                        <Toast.Body>{answerMessage}</Toast.Body>
                    </Toast>
                </Col>
            </Row> 
        </Form>
    );
};

export default AnswerBox;
