import { React, useState, useEffect } from "react";
import { Form, Button, Table, Toast } from "react-bootstrap";

function AnswerBox () {
    const [answerMessage, setAnswerMessage] = useState("");
    const [userAnswer, setUserAnswer] = useState([]);
    const [showToast, setShowToast] = useState(false);

    const toggleShowToast = () => setShowToast(!showToast);

    const checkAnswer = () => {
        const correctAnswer = 53.1;
        console.log(userAnswer);
        setUserAnswer(userAnswer);
        console.log(userAnswer);
        if (userAnswer <= correctAnswer + 0.1 && userAnswer >= correctAnswer - 0.1) {
            setAnswerMessage("Correct! Try another problem!")
        
        } else {
            setAnswerMessage("Incorrect. Please try again!")
        };
    };

    return (
        <Table size="sm">
            <thead>
                <tr>
                    <th colSpan="2">
                        Solve for angle b (in degrees)
                    </th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                        Answer =
                    </td>
                    <td>
                        <Form.Label className="mx-auto">
                            <Form.Control
                                required
                                placeholder="Round to 1 decimal. (e.g. 10.3)"
                                type="number"
                                value={userAnswer}
                                onChange={(e) => 
                                    setUserAnswer(e.target.value)}
                                >
                            </Form.Control>
                        </Form.Label>
                    </td>
                </tr>
                <tr>
                    <td colSpan="2">
                        <Button
                            onSubmit={(submittedAnswer) => checkAnswer(submittedAnswer)}
                            onClick={toggleShowToast}
                            >
                            Check Answer
                        </Button>
                        <Toast 
                            show={showToast}
                            onClose={toggleShowToast}
                            >
                            <Toast.Body>{answerMessage}</Toast.Body>
                        </Toast>
                    </td>   
                </tr>
            </tbody>
        </Table>
    );
};

export default AnswerBox;
