import { Col, Row, Container, Stack, Tooltip, Button, OverlayTrigger, Modal } from "react-bootstrap";
import { React, useState, useEffect } from "react";
import Triangle from "../components/Triangle.PNG";
import SCTFormulas from "../components/SCTFormulas.js";
import TriangleDimensions from "../components/TriangleDimensions";
import AnswerBox from "../components/AnswerBox";
import Hints from "../components/Hints";

function TrigRatios( ) {

    const [triangle, setTriangle] = useState({});
    const [showModal, setShowModal] = useState(false);

    const handleCloseModal = () => setShowModal(false);
    const handleShowModal = () => setShowModal(true);

    const tooltip = (props) => (
        <Tooltip id="button-tooltip" {...props}>
            Be careful - you will not be able to return to this exact problem.
        </Tooltip>
    );

    const generateProblem = async () => {
        const response = await fetch("/new-problem", {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        });
        const triangle = await response.json();
        setTriangle(triangle);
    };

    useEffect(() => {
        generateProblem();
    }, []);

    return (
        <Container fluid>
            <h1>Trigonometric Ratios Practice</h1>
            <Row>
                <Col sm={8}><img src={Triangle} /></Col>
                <Col sm={4}>
                    <Stack gap={4}>
                        <SCTFormulas></SCTFormulas>
                        <br></br>
                        <TriangleDimensions triangle={triangle}></TriangleDimensions>
                    </Stack>
                </Col>
            </Row>
            <br></br>
            <Row>
                <Col sm={{ span: 3, offset: 1 }}>
                    <Stack gap={5}>
                        <Hints></Hints>
                        <Button onClick={handleShowModal}></Button>    
                        <Modal
                            show={showModal}
                            size="lg"
                            centered
                        >
                            <Modal.Body>
                                <h3>
                                    Would you like to generate a new problem?
                                </h3>
                                <p>
                                    You will lose the current problem, and will not be able to
                                    return to it.
                                </p>
                            </Modal.Body>
                            <Modal.Footer>
                                <Button
                                    type="submit"
                                    onClick={handleCloseModal}
                                >Cancel - Return to problem</Button>
                                <Button 
                                    type="submit"
                                    onClick={generateProblem}
                                    variant="warning"
                                >Give me a new problem</Button>
                            </Modal.Footer>
                        </Modal>
                        
                    </Stack>
                </Col>
                <Col sm={{ span: 6, offset: 1 }}>
                    <AnswerBox triangle={triangle}></AnswerBox>
                </Col>
            </Row>
        </Container>
    )
};

export default TrigRatios;
