import { React, useState, useEffect } from 'react';
import { Table, Button } from "react-bootstrap";

function TriangleDimensions ( {triangle} ) {

    const [angleA, setAngleA] = useState(triangle.problemAngleA);
    const [angleB, setAngleB] = useState(triangle.problemAngleB);
    const [angleC, setAngleC] = useState(triangle.problemAngleC);
    const [sideA, setSideA] = useState(triangle.problemSideA);
    const [sideB, setSideB] = useState(triangle.problemSideB);
    const [sideC, setSideC] = useState(triangle.problemSideC);

    const showDimensions = () => {
        
    }

    const showResults = (event) => {
        event.preventDefault();

        setAngleA(triangle.angleA);
        setAngleB(triangle.angleB);
        setAngleC(triangle.angleC);
        setSideA(triangle.sideA);
        setSideB(triangle.sideB);
        setSideC(triangle.sideC);
    };

    useEffect(() => {
        generateProblem();
    }, []);

    return (
        <div>
            <Table bordered>
                <thead>
                    <tr>
                        <th colSpan="2">
                            Triangle Dimensions
                        </th>
                    </tr>
                    <tr>
                        <th>
                            Side/Angle
                        </th>
                        <th>
                            Measurement
                        </th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>side A</td>
                        <td>{sideA}</td>
                    </tr>
                    <tr>
                        <td>side B</td>
                        <td>{sideB}</td>
                    </tr>
                    <tr>
                        <td>side C</td>
                        <td>{sideC}</td>
                    </tr>
                    <tr>
                        <td>angle a</td>
                        <td>{angleA}<span>&#176;</span></td>
                    </tr>
                    <tr>
                        <td>angle b</td>
                        <td>{angleB}<span>&#176;</span></td>
                    </tr>
                    <tr>
                        <td>angle c</td>
                        <td>{angleC}<span>&#176;</span></td>
                    </tr>
                    <tr>
                    </tr>
                </tbody>           
            </Table>
            <div className="d-grid">
                <Button
                    type="submit"
                    variant="warning"
                    onClick={showResults}
                >
                    Show all answers
                </Button>
            </div>
        </div>
    )
};

export default TriangleDimensions;
